mapper.select:
select configs.VIDEOSERVICE,configs.VOICESERVICE,configs.IMAGESERVICE,doc.id as docid,doc.name as docname,doc.begood,rice.graphicprice,rice.speechprice,rice.videoprice,chart.titleName,hpath.FTPURL||doc.PHOTOID as ftpurl,luate.score from HEALTHDOCTOR doc
left join (select count(*) as ordersum,docid from HealthyOrder GROUP BY docid ) orders on doc.id=orders.docid
left join (select count(*) as ordersum,docid from HealthyOrder GROUP BY docid order by ordersum desc ) orders on doc.id=orders.docid
left join HealthServiceConfig configs on doc.id=configs.docid left join HEALTHPRICE rice on doc.id=rice.docid left join healthTitleChart chart on doc.titlecode=chart.TITLECODE left join HEALTHFTPPATH hpath on doc.PATHCODE=hpath.PATHCODE left join (select round(avg(score),1) as score,docid from healthevaluate GROUP BY docid) luate on doc.id=luate.docid
doc.name like '%'||#{docname,jdbcType=VARCHAR}||'%'
and rice.GRAPHICPRICE BETWEEN #{onevalue,jdbcType=VARCHAR} AND #{twovalue,jdbcType=VARCHAR}
and rice.SPEECHPRICE BETWEEN #{onevalue,jdbcType=VARCHAR} AND #{twovalue,jdbcType=VARCHAR}
and rice.VIDEOPRICE BETWEEN #{onevalue,jdbcType=VARCHAR} AND #{twovalue,jdbcType=VARCHAR}
order by luate.score desc

mapper.update:
update HEALTHCONSULTATION set CONSULTATIONSUMMARY = #{consultationsummary,jdbcType=VARCHAR}, UPDATEDATE = #{updatedate,jdbcType=TIMESTAMP},
UPDATENAME = #{updatename,jdbcType=VARCHAR},
CONSULTATIONSTARTTIME = #{consultationstarttime,jdbcType=TIMESTAMP}, CONSULTINGSTATE=#{consultingstate,jdbcType=VARCHAR} where ID = #{id,jdbcType=CHAR} and USRID = #{usrid,jdbcType=CHAR} and DOCID = #{docid,jdbcType=CHAR}

update HEALTHCONSULTATION
set
  CONSULTATIONSTARTTIME = #{consultationstarttime,jdbcType=TIMESTAMP}
where ID = #{id,jdbcType=CHAR} and DOCID = #{docid,jdbcType=CHAR}

mapper.select:
select a.id, d.RESERVATIONDATE, b.id usrids, b.name usrname, b.guid usrid, a.docid, a.consultingstate, b.imgguid, a.id consultationid, a.ORDERID, a.CONSULTATIONSUMMARY, d.RESERVATIONDATE || ' ' || d.ReserStartTime || '-' || d.ReserEndTime as RESERVATIONPERIOD, d.consultationmethod, d.RESERVATIONDATE || ' ' || d.ReserEndTime as ReserEndTime, d.consultingdescription, d.RESERVATIONPHOTOID, hp.FTPURL as ftpurl from HEALTHCONSULTATION a, usr b, healthDoctor c, HealthyOrder d left join HEALTHFTPPATH hp on hp.PATHCODE = d.PATHCODE where a.usrid = b.guid and a.docid = c.id and a.orderid = d.id and a.DOCID = #{docid, jdbcType = CHAR} and a.state = '1' and d.state = '1'
and d.consultationmethod = #{consultationmethod, jdbcType = CHAR}
and a.consultingstate = #{consultingstate, jdbcType = CHAR}
AND To_date(d.RESERVATIONDATE || '-' || d.ReserEndTime,'yyyy-MM-dd HH24:mi') >= to_date(to_char(sysdate, 'yyyy-MM-dd HH24:mi'), 'yyyy-MM-dd HH24:mi') and d.ORDERSTATE = '2' order by d.RESERVATIONDATE ASC, d.ReserStartTime ASC
and d.ORDERSTATE in ( '5', '8') order by d.RESERVATIONDATE desc,d.ReserStartTime desc

select d.reservationperiod intervaldate , a.orderid,a.usrid,a.docid,a.consultingstate, b.name usrname, decode(mod(to_number(substr(b.idcard, 17, 1)), 2), 0, '2', '1') as sex, trunc(to_char(sysdate, 'yyyy') - to_char(TO_DATE(SUBSTR(b.idcard, 7, 4), 'YYYY'), 'yyyy')) / 1 age, b.id usrphone, c.name docname, d.reservationdate ,d.consultationmethod, d.reservationtime from HealthConsultation a, usr b , healthDoctor c , HealthyOrder d where a.usrid=b.guid and a.docid = c.id and a.orderid =d.id and a.state='1'
and d.reservationdate like '${ reservationdate + '%'}'
and d.consultationmethod = #{consultationmethod,jdbcType=VARCHAR}
and a.consultingstate = #{consultingstate,jdbcType=VARCHAR}
order by d.reservationdate , reservationdate desc
