mapper.insert:
insert into CHIS_FOLLOWUP_PLAN (GUID, ORG, DOCGUID, 
  USRGUID, SICGUID, FOLLOWOPTION, 
  FOLLOWDATETIME, FOLLOWSTATE, CRTUSR, 
  CRTDAT, UPDATEUSR, UPDATEDATE
  )
values (#{guid,jdbcType=VARCHAR}, #{org,jdbcType=VARCHAR}, #{docguid,jdbcType=VARCHAR}, 
  #{usrguid,jdbcType=VARCHAR}, #{sicguid,jdbcType=VARCHAR}, #{followoption,jdbcType=VARCHAR}, 
  #{followdatetime,jdbcType=TIMESTAMP}, #{followstate,jdbcType=VARCHAR}, #{crtusr,jdbcType=VARCHAR}, 
  #{crtdat,jdbcType=TIMESTAMP}, #{updateusr,jdbcType=VARCHAR}, #{updatedate,jdbcType=TIMESTAMP}
  )

mapper.update:
 update CHIS_FOLLOWUP_PLAN

    set
    FOLLOWSTATE = #{followstate,jdbcType=VARCHAR}

where GUID = #{guid,jdbcType=VARCHAR}

mapper.select:
select * from ( select * from (select c.followoption,to_char(c.followdatetime,'YYYY-MM-DD HH24:MI') as followdatetime, c.guid as sfjhguid, c.docguid from (select * from CHIS_followup_Plan where status='1' and usrguid =#{usrguid,jdbcType=VARCHAR}
and FOLLOWDATETIME>=to_date(to_char(sysdate,'yyyy-MM-dd'),'yyyy-MM-dd') and followstate in ('1','2')
and followstate in ('3','4','5','6','7')
) c left join usr u on c.usrguid = u.guid ) m left join (select dc.*,v.name as deptnam from (select y.name, y.hospital,y.department, u.guid from usr u, yx_doc y where u.id = y.id) dc left join V_YX_DOCDEPT v on dc.department=v.id ) n on m.docguid = n.guid) order by followdatetime desc

mapper.update:
	 update CHIS_FOLLOWUP_PLAN
     set FOLLOWSTATE = '1'
    where DOCGUID = #{docguid,jdbcType=VARCHAR} and USRGUID = #{usrguid,jdbcType=VARCHAR}
